    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        // TODO add your handling code here:
        String empName;
        double salPerHour, workHours, totlSalary;
        
        empName = txtNombre.getText();
        salPerHour = Double.parseDouble(txtSalarioHora.getText());
        workHours = Double.parseDouble(txtHorasMes.getText());
        
        totlSalary = workHours * salPerHour;
        
        if (totlSalary > 450000){
            txtResultado.setText(empName + " gan√≥ $" + totlSalary + " en el mes");
        }
        else{
            txtResultado.setText(empName);
        }
    }//GEN-LAST:event_btnCalcularActionPerformed

