    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        // TODO add your handling code here:
        
        double pA, pB, pC, discriminante, x1, x2, x;
        
        pA = Double.parseDouble(txtParamA.getText());
        pB = Double.parseDouble(txtParamB.getText());
        pC = Double.parseDouble(txtParamC.getText());
        
        discriminante = pB * pB - 4 * pA * pC;
        
        if (discriminante > 0) {
            x1 = (-pB + Math.sqrt(discriminante)) / (2 * pA);
            x2 = (-pB - Math.sqrt(discriminante)) / (2 * pA);
            txtResultado.setText("Las soluciones son: x1 = " + x1 + " y x2 = " + x2);
        } else if (discriminante == 0) {
            x = -pB / (2 * pA);
            txtResultado.setText("La soluci√≥n es: x = " + x);
        } else {
            txtResultado.setText("No hay soluciones reales.");
        }
    }//GEN-LAST:event_btnCalcularActionPerformed

